INSERT INTO `callboard` (`id`, `title`, `author`, `content`, `create_time`, `update_time`, `start_time`, `end_time`, `url`, `cb_type`) VALUES (1,'积粒网全新上线','积粒网运营中心','经过紧张筹备和开发，积粒网终于在2013年6月6日与大家见面啦！<br/><br/>积粒网是由网曦信息科技（上海）有限公司运营的全新积分媒体，隶属于VOYAGE GROUP。凭借在日本运营比价购物网站ECnavi的近15年经验和技术积累，希望带给中国用户真正的一站式积分解决方案！<br/><br/>前期，会员通过积粒网的各种活动，可以从日常的上网行为中获得积分（米粒），如体验某些互联网广告、在购物网站购物、试玩游戏等，进而将米粒兑换成现金、手机费、购物礼券及各种其他奖励。<br/><br/>今后，积粒网还将成为一个积分的兑换平台，您可以把其他网站上累计的积分兑换到积粒网，如航空公司积分、银行积分、旅游网站积分等，从而解决小额积分浪费和分散的问题。<br/><br/>我们将本着以会员为中心的运营理念，全心为会员提供服务，实现“赚积分，攒积分，花积分”一站搞定！<br/>请广大网友支持积粒网，积极参与积粒网的活动，多提宝贵意见！','2013-06-08 10:20:11',NULL,'2013-06-06 00:00:00','2013-06-08 10:20:11',NULL,1),(2,'关于兑换中心的说明','积粒网运营中心','积粒网已经与2013年6月6日正式上线，目前积分兑换功能暂时尚未开放。<br/><br/>我们将尽快开放积分兑换功能，请各位会员保持耐心。您帐户内的积分在兑换功能开放前不会失效。<br/><br/>初期，积粒网的积分将只能兑换成“91问问调查网”www.91wenwen.net的积分，兑换比例为1:1。在兑换成91问问积分后，您可以在91问问调查网进一步选择其他相关奖品或现金。<br/><br/>今后，积粒网将提供直接兑换成现金、手机费、购物卡、Q币、实物奖励等，敬请期待！','2013-06-08 10:21:10',NULL,'2013-06-06 00:00:00','2013-06-08 10:21:10',NULL,1),(3,'8/8 12:00~18:00 系统维护','积粒网运营中心 ','亲爱的用户！<br />\r\n<br />\r\n积粒网将于2013/8/8 12:00~18:00 进行系统维护，届时网站将不能正常访问。<br />\r\n对于大家造成的不便感到万分抱歉，希望维护后积粒网将能带给您更好的体验。<br />\r\n<br />\r\n2013/8/5<br />\r\n积粒网运营中心 ','2013-08-05 11:13:39','2013-08-07 09:11:43','2013-08-05 00:00:00','2013-08-05 11:13:39',NULL,1),(4,'8/7 购物返利体验广告Bug通告','积粒网运营中心','亲爱的用户！<br />\r\n<br />\r\n　　由于系统Bug，现在发生有些购物返利和体验广告的返利积分为0，为您造成不便，深感保险。<br />\r\n　　我们一定尽快修复该Bug，并承诺返回您应得的积分。<br />\r\n　　修复后会另发公告通知，敬请等待，谢谢您对积粒网的支持！<br />\r\n<br />\r\n积粒网运营中心<br />\r\n','2013-08-07 09:09:54','2013-08-07 09:12:22','2013-08-07 00:00:00','2013-08-07 09:09:54',NULL,1),(5,'8/7 Bug修复通告','积粒网运营中心','亲爱的用户！<br />\r\n<br />\r\n　　购物返利和体验广告的返利积分为0的Bug已经修复，之前由于Bug而造成的积分也已经修改。<br />\r\n　　如果您之前有遇到Bug造成的影响，请您再次确认一下您的任务历史。<br />\r\n　　如果还有问题，请发邮件至<a href=\"mailto:91jili@hotmail.com\">cs@91jili.com</a> 。<br />\r\n　　再次感谢您对积粒网的支持！<br />\r\n<br />\r\n积粒网运营中心','2013-08-07 11:46:41','2013-08-07 11:47:34','2013-08-07 00:00:00','2013-08-07 11:46:41',NULL,1),